awk 'BEGIN{num=0;total=0;createfile=0;createtable;insertdata=0;tablefinish=0;filesync=0;fileclose=0}{if($0~"createfile"){num += 1;createfile += $2;createtable += $4;insertdata += $6;tablefinish += $8;filesync += $10;fileclose += $12;total += $2 + $4 + $6 + $8 + $10 + $12}}END{print createfile/num " " createtable/num " " insertdata/num " " tablefinish/num " " filesync/num " " fileclose/num " " total/num "\n" createfile/total*100 " " createtable/total*100 " " insertdata/total*100 " " tablefinish/total*100 " " filesync/total*100 " " fileclose/total*100 " " total/total*100}' $1

